
public class oddeven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 29;


	     //checking whether the number is even or odd
	     if (number % 2 == 0)
	              System.out.println(number + " is Even");
	     else
	              System.out.println(number + " is odd");
	      
	 

	}

}
